import {ChangeDetectionStrategy, ChangeDetectorRef, Component, EventEmitter, Input, OnChanges, Output} from '@angular/core';
import {StructureTemplate} from '../../../protos';
import {ContentChange} from 'ngx-quill/lib/quill-editor.component';
import {DeltaOperation} from 'quill';
import StructureTemplateBeat = StructureTemplate.StructureTemplateBeat;

import {debounce} from 'debounce';

export interface StructureTemplateUpdate {
  structureTemplate: StructureTemplate;
  modifiesListView: boolean;
}

@Component({
  selector: 'app-structure-template-details',
  templateUrl: './structure-template-details.component.html',
  styleUrls: ['./structure-template-details.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class StructureTemplateDetailsComponent implements OnChanges {

  constructor(private ref: ChangeDetectorRef) {}
  @Input()
  structureTemplate: StructureTemplate|null = null;

  @Output() templateUpdated = new EventEmitter<StructureTemplateUpdate>();

  quillContent: DeltaOperation[] = [];
  errorMessage = '';
  lastStructureTemplateInput: StructureTemplate|null = null;

  onContentChanged = debounce((contentChangeEvent: ContentChange) => {
    this.parseContent(contentChangeEvent.text + '\n');
  }, 200);

  ngOnChanges(): void {
    if (this.areStructureTemplatesEqual(this.lastStructureTemplateInput, this.structureTemplate)) {
      // No reason to update
      return;
    }

    this.lastStructureTemplateInput = this.structureTemplate;
    this.refreshQuillContent();
  }

  refreshQuillContent(): void {
    this.quillContent = [];

    this.quillContent.push(
      getQuillHeader('Name')
    );

    this.quillContent.push({
      insert: this.structureTemplate?.name + '\n',
      attributes: {},
    });

    this.quillContent.push(
      getQuillHeader('Description')
    );

    this.quillContent.push({
      insert: this.structureTemplate?.description + '\n',
      attributes: {},
    });

    this.quillContent.push(
      getQuillHeader('Beats')
    );

    if (!!this.structureTemplate?.beats) {
      const beats = this.structureTemplate.beats;
      for (let i = 0; i < beats.length; i++) {

        this.quillContent.push(
          getQuillSubHeader('Description')
        );

        this.quillContent.push({
          insert: beats[i].description + '\n',
          attributes: {},
        });

        this.quillContent.push(
          getQuillSubHeader('Duration')
        );

        this.quillContent.push({
          insert: beats[i].intendedDurationMs + '\n',
          attributes: {},
        });

        if (i + 1 < beats.length) {
          this.quillContent.push(
            getQuillSeparator()
          );
        }
      }
    }
    this.ref.markForCheck();
  }

  isEmpty(): boolean {
    return this.structureTemplate == null;
  }

  parseContent(newContent: string): void {

    const fieldMap = parseFields(newContent, false);

    let parsedBeats: Map<string, string>[] = [];
    if (fieldMap.has('Beats')) {
      parsedBeats = splitRepeatedFields(fieldMap.get('Beats') || '')
        .map((beatText) => {
        return parseFields(beatText, true);
      }).filter(Boolean);
    }

    this.errorMessage = this.getErrorMessage(fieldMap, parsedBeats);
    if (this.errorMessage !== '') {
      this.ref.markForCheck();
      return;
    }

    const newStructureTemplate = StructureTemplate.create({
      id: this.structureTemplate?.id,
      name: fieldMap.get('Name'),
      description: fieldMap.get('Description'),
      beats: parsedBeats.map((beatMap) => {
        return StructureTemplateBeat.create({
          description: beatMap.get('Description'),
          intendedDurationMs: parseInt(beatMap.get('Duration') || '', 10)
        });
      })
    });

    if (this.areStructureTemplatesEqual(newStructureTemplate, this.structureTemplate)) {
      // No meaningful difference
      return;
    }

    this.lastStructureTemplateInput = newStructureTemplate;

    this.templateUpdated.emit({
      structureTemplate: newStructureTemplate,
      modifiesListView: newStructureTemplate.name !== this.structureTemplate?.name
    } as StructureTemplateUpdate);

    this.ref.markForCheck();
  }

  getErrorMessage(fieldMap: Map<string, string>, parsedBeats: Map<string, string>[]): string {
    if (!fieldMap.has('Name')) {
      return 'Missing "Name" field';
    }

    if (fieldMap.get('Name')?.trim().length === 0) {
      return '"Name" cannot be empty';
    }

    if (!fieldMap.has('Description')) {
      return 'Missing "Description" field';
    }

    for (const beat of parsedBeats) {
      if (!beat.has('Description')) {
        return 'Beat missing "Description" field';
      }

      if (!beat.has('Duration')) {
        return 'Beat missing "Duration" field';
      }

      if (! parseInt(beat.get('Duration')?.trim() || '', 10)) {
        return 'Cannot parse "Duration" value to integer';
      }
    }

    return '';
  }

  areStructureTemplatesEqual(one: StructureTemplate|null, two: StructureTemplate|null): boolean {
    if (one == null && two == null) {
      return true;
    }

    if (one == null || two == null) {
      return false;
    }

    if (one.name !== two.name) {
      return false;
    }

    if (one.id !== two.id) {
      return false;
    }

    if (one.description !== two.description) {
      return false;
    }

    if (one.beats.length !== two.beats.length) {
      return false;
    }

    const beatsLength = one.beats.length;
    for (let i = 0; i < beatsLength; i++) {
      if (one.beats[i].description !== two.beats[i].description) {
        return false;
      }
      if (one.beats[i].intendedDurationMs !== two.beats[i].intendedDurationMs) {
        return false;
      }
    }

    return true;
  }

  repair(): void {
    this.refreshQuillContent();
  }

  addBeat(): void {
    this.structureTemplate?.beats.push(
      StructureTemplateBeat.create({
        description: 'My new beat',
        intendedDurationMs: 12
      })
    );

    this.templateUpdated.emit({
      structureTemplate: this.structureTemplate,
      modifiesListView: false
    } as StructureTemplateUpdate);
    this.refreshQuillContent();
  }
}

function parseFields(raw: string, isSubfields: boolean): Map<string, string> {
  const regexStr = isSubfields
    ? '--([^-]+)--\n([^-]+)'
    : '==([^=]+)==\n([^=]+)';

  const rx = RegExp(regexStr, 'gm');

  const fieldMap: Map<string, string> = new Map();

  let match;
  // tslint:disable-next-line:no-conditional-assignment
  while ((match = rx.exec(raw)) !== null) {
    const key = match[1].trim();
    const value = match[2].trim();
    fieldMap.set(key, value);
  }

  return fieldMap;
}

function splitRepeatedFields(raw: string): string[] {
  return raw.split(/\n[-]+--\n/);
}

function getQuillHeader(headerName: string): DeltaOperation {
  return {
    insert: '\n== ' + headerName + ' ==\n',
    attributes: {
      bold: true
    }
  };
}

function getQuillSubHeader(headerName: string): DeltaOperation {
  return {
    insert: '\n-- ' + headerName + ' --\n',
    attributes: {
      italic: true
    }
  };
}

function getQuillSeparator(): DeltaOperation {
  return {
    insert: '\n' + '-'.repeat(20) + '\n',
    attributes: {
      italic: true
    }
  };
}

import { Injectable } from '@angular/core';
import { v4 as uuidv4 } from 'uuid';
import {StorageService} from './storage.service';
import {ITag, Tag} from '../protos';

// Minimal details about a template to show in list view
export interface TagListView {
  id: string;
  name: string;
}

const ALL_TAG_LIST_KEY = 'allTagList';
const TAG_KEY_PREFIX = 'tag-';

@Injectable({
  providedIn: 'root'
})
export class TagService {

  constructor(private storageService: StorageService) { }

  async getAllTags(): Promise<Tag[]> {
    const allEntries = await this.getAllTagsListView();

    const allTags: Tag[] = [];

    for (const entry of allEntries) {
      const fetchedTag = await this.getTag(entry.id);
      if (fetchedTag != null) {
        allTags.push(
          fetchedTag
        );
      }
    }

    return allTags;
  }

  async getAllTagsListView(): Promise<TagListView[]> {
    return (await this.storageService.get(ALL_TAG_LIST_KEY) || []) as TagListView[];
  }

  getTagKey(uuid: string): string {
    return TAG_KEY_PREFIX + uuid;
  }

  async getTag(uuid: string): Promise<Tag|null> {
    const fetchedData = (await this.storageService.get(this.getTagKey(uuid))) as Uint8Array;

    if (!fetchedData) {
      console.error('Could not find structure template: ' + uuid);
      return null;
    }

    return Tag.decode(
      fetchedData
    );
  }

  subscribeToTagListView(handler: (newTagListView: TagListView[]) => void): string {
    return this.storageService.generateSubscription(ALL_TAG_LIST_KEY, (fetchedValue) => {
      handler((fetchedValue || []) as TagListView[]);
    });
  }

  subscribeToTag(tagId: string, handler: (newTag: Tag|null) => void): string {
    return this.storageService.generateSubscription(this.getTagKey(tagId), (fetchedValue) => {
      handler(
        Tag.decode(
          fetchedValue as Uint8Array
        )
      );
    });
  }

  async deleteTag(tagId: string): Promise<void> {
    await this.storageService.delete(
      this.getTagKey(tagId),
      true
    );

    const listViewEligible = (await this.getAllTagsListView())
      .filter((tagListView) => {
        return tagListView.id !== tagId;
      });

    await this.setAllTagsListView(listViewEligible);
  }

  cancelSubscription(subscription: string): void {
    this.storageService.cancelSubscription(subscription);
  }

  async setAllTagsListView(tags: ITag[]): Promise<void> {
    const listView = tags.map((tag: ITag) => {
      return {
        id: tag.id,
        name: tag.name,
      } as TagListView;
    });

    await this.storageService.set(ALL_TAG_LIST_KEY, listView, true);
  }

  async refreshAllTagsListView(): Promise<void> {
    const allTags = await this.getAllTags();

    await this.setAllTagsListView(
      allTags
    );
  }

  async setAllTags(tags: ITag[]): Promise<void> {
    await this.setAllTagsListView(tags);

    for (const tag of tags) {
      await this.setTag(tag as Tag);
    }
  }

  async setTag(tag: Tag, affectsListView: boolean = false): Promise<void> {
    await this.storageService.set(
      this.getTagKey(tag.id),
      Tag.encode(tag).finish(),
      true
    );

    if (affectsListView) {
      await this.refreshAllTagsListView();
    }
  }

  // Creates a new structure template, and returns the uuid of the new entry
  async createNewTag(): Promise<string> {
    const uuid = uuidv4();

    const newTag = Tag.create({
      id: uuid,
      name: 'My new tag',
      description: 'Description goes here',
      hasNumericValue: true,
    });

    await this.setTag(newTag);

    const allTags = await this.getAllTags();
    allTags.push(newTag);

    await this.setAllTagsListView(allTags);

    return uuid;
  }
}

import {ChangeDetectionStrategy, Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import {Tag} from '../../../protos';
import {MatCheckboxChange} from '@angular/material/checkbox';

import {debounce} from 'debounce';

export interface TagUpdate {
  tag: Tag;
  modifiesListView: boolean;
}

@Component({
  selector: 'app-tag-details',
  templateUrl: './tag-details.component.html',
  styleUrls: ['./tag-details.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class TagDetailsComponent implements OnInit {

  constructor() { }

  @Input()
  tag: Tag|null = null;

  @Output() tagUpdated = new EventEmitter<TagUpdate>();

  onNameInput = debounce((event: any) => {
    const tag = this.tag as Tag;

    tag.name = event.target.value;

    this.tagUpdated.emit({
      tag,
      modifiesListView: true,
    } as TagUpdate);
  }, 200);

  onDescriptionInput = debounce((event: any) => {
    const tag = this.tag as Tag;

    tag.description = event.target.value;

    this.tagUpdated.emit({
      tag,
      modifiesListView: false,
    } as TagUpdate);
  }, 200);

  setEnumLabel = debounce((index: number, event: any) => {
    const tag = this.tag as Tag;

    tag.enumValues[index].label = event.target.value;

    this.tagUpdated.emit({
      tag,
      modifiesListView: false,
    } as TagUpdate);
  }, 200);

  ngOnInit(): void {
  }

  hasNumericValue(): boolean {
    return this.tag?.hasNumericValue ?? false;
  }

  onHasNumericValueCheckbox(event: MatCheckboxChange): void {
    const tag = this.tag as Tag;

    tag.hasNumericValue = event.checked;

    this.tagUpdated.emit({
      tag,
      modifiesListView: false,
    } as TagUpdate);
  }

  isEmpty(): boolean {
    return this.tag == null;
  }

  setDeprecatedEnum(index: number, event: MatCheckboxChange): void {
    const isDeprecated = !event.checked;

    const tag = this.tag as Tag;

    tag.enumValues[index].isDeprecated = isDeprecated;

    this.tagUpdated.emit({
      tag,
      modifiesListView: false,
    } as TagUpdate);
  }

  addEnumValue(): void {
    const enumValue = {
      label: 'My enum option',
      isDeprecated: false,
    } as Tag.EnumValue;

    const tag = this.tag as Tag;

    tag.enumValues.push(enumValue);

    this.tagUpdated.emit({
      tag,
      modifiesListView: false,
    } as TagUpdate);
  }
}

