import { Injectable } from '@angular/core';
import {StorageService} from './storage.service';
import {Beat, IBeat} from '../protos';
import {epochMsToTimestamp, timestampToEpochMs} from './timestamp-helpers';
import {v4 as uuidv4} from 'uuid';

export interface BeatMapView {
  id: string;
  name: string;
  lastUpdated: number;
  parentBeats: string[]; // IDs of any beats which include this one in the structure or brainstorm
  intendedDurationMs: number;
  completeness: Beat.Completeness;
}

export interface BeatReadView {
  id: string;
  name: string;
  prose: string;
  showExpanded: boolean;
  completeness: Beat.Completeness;
}

const ALL_BEAT_MAP_KEY = 'allBeatMap';
const BEAT_KEY_PREFIX = 'beat-';

@Injectable({
  providedIn: 'root'
})
export class BeatsService {

  constructor(private storageService: StorageService) { }

  private static getInitialMapVersion(beat: IBeat): BeatMapView {
    return {
      id: beat.id,
      name: beat.synopsis,
      lastUpdated: timestampToEpochMs(beat.lastUpdated),
      parentBeats: [],
      intendedDurationMs: beat.intendedDurationMs,
      completeness: beat.completeness
    } as BeatMapView;
  }

  private static getBeatKey(uuid: string): string {
    return BEAT_KEY_PREFIX + uuid;
  }

  async setAllBeats(beats: IBeat[]): Promise<void> {
    await this.setBeatMap(
      this.generateBeatMap(beats)
    );

    for (const beat of beats) {
      await this.setBeat(beat as Beat, false, false);
    }
  }

  async createNewBeat(): Promise<string> {
    const uuid = uuidv4();

    const newBeat = Beat.create({
      id: uuid,
      synopsis: 'My new beat',
      prose: '',
      intendedDurationMs: 60 * 1000,
      structure: [],
      brainstorm: [],
    });

    await this.setBeat(newBeat, true, true);

    return uuid;
  }

  async deleteBeat(beatId: string): Promise<void> {
    const beat = await this.getBeat(beatId) as Beat;
    let beatMap = await this.getBeatMap();

    // Remove any references to this beat as child
    const parentIds = beatMap.get(beatId)?.parentBeats as string[];
    for (const parentId of parentIds) {
      const parentBeat = await this.getBeat(parentId) as Beat;
      parentBeat.brainstorm = parentBeat.brainstorm.filter(brainstormId => brainstormId !== beatId);
      parentBeat.structure = parentBeat.structure.filter(structureId => structureId !== beatId);
      await this.setBeat(parentBeat, true, true);
    }

    // Re-fetch the beat map, to pick up all the recent changes
    beatMap = await this.getBeatMap();

    // Remove this beat as "parent" in any children
    BeatsService.removeIdFromParents(
      beatMap,
      beatId,
      (beat.structure || [])
    );
    BeatsService.removeIdFromParents(
      beatMap,
      beatId,
      (beat.brainstorm || [])
    );
    beatMap.delete(beatId);
    await this.setBeatMap(beatMap);

    await this.storageService.delete(
      BeatsService.getBeatKey(beatId),
      true
    );
  }

  async setBeat(beat: Beat, affectsMapView: boolean = false, affectsLastUpdated: boolean = true): Promise<void> {
    if (affectsLastUpdated) {
      beat.lastUpdated = epochMsToTimestamp(Date.now());
    }

    if (affectsMapView) {
      const originalBeat = await this.getBeat(beat.id) as Beat;
      const beatMap = await this.getBeatMap();

      const originalBrainstorm = (originalBeat?.brainstorm || []);
      const originalStructure = (originalBeat?.structure || []);

      // Determine if there are any changes in the children
      if (!allStringsInBothArrays(beat.brainstorm, originalBrainstorm)
        || !allStringsInBothArrays(beat.structure, originalStructure)) {
        BeatsService.removeIdFromParents(
          beatMap,
          beat.id,
          originalBrainstorm
        );
        BeatsService.removeIdFromParents(
          beatMap,
          beat.id,
          originalStructure
        );

        BeatsService.addParentToChildren(
          beatMap,
          beat.id,
          beat.brainstorm
        );
        BeatsService.addParentToChildren(
          beatMap,
          beat.id,
          beat.structure
        );
      }

      const mapView = BeatsService.getInitialMapVersion(beat);
      mapView.parentBeats = beatMap.get(beat.id)?.parentBeats || [];

      beatMap.set(beat.id, mapView);
      await this.setBeatMap(beatMap);
    }

    await this.storageService.set(
      BeatsService.getBeatKey(beat.id),
      Beat.encode(beat).finish(),
      true
    );
  }

  async getBeat(uuid: string): Promise<Beat|null> {
    const fetchedData = (await this.storageService.get(BeatsService.getBeatKey(uuid))) as Uint8Array;

    if (!fetchedData) {
      console.error('Could not find beat: ' + uuid);
      return null;
    }

    return Beat.decode(
      fetchedData
    );
  }

  async getAllBeats(): Promise<Beat[]> {
    const beatMap = await this.getBeatMap();

    const allBeats: Beat[] = [];

    for (const uuid of beatMap.keys()) {
      const fetchedBeat = await this.getBeat(uuid);
      if (fetchedBeat != null) {
        allBeats.push(
          fetchedBeat
        );
      }
    }

    return allBeats;
  }

  private async setBeatMap(beatMap: Map<string, BeatMapView>): Promise<void> {
    await this.storageService.set(ALL_BEAT_MAP_KEY, beatMap, true);
  }

  private generateBeatMap(beats: IBeat[]): Map<string, BeatMapView> {
    const beatMap = new Map<string, BeatMapView>();

    beats.forEach((beat: IBeat) => {
      beatMap.set(
        beat.id as string,
        BeatsService.getInitialMapVersion(beat)
      );
    });

    // Set parent references
    beats.forEach((beat: IBeat) => {
      const id = beat.id as string;

      BeatsService.addParentToChildren(
        beatMap,
        id,
        (beat.brainstorm || [])
      );
      BeatsService.addParentToChildren(
        beatMap,
        id,
        (beat.structure || [])
      );
    });

    return beatMap;
  }

  async getBeatMap(): Promise<Map<string, BeatMapView>> {
    return (await this.storageService.get(ALL_BEAT_MAP_KEY) || new Map()) as Map<string, BeatMapView>;
  }

  subscribeToBeatMapView(handler: (newBeatMapView: Map<string, BeatMapView>) => void): string {
    return this.storageService.generateSubscription(ALL_BEAT_MAP_KEY, (fetchedValue) => {
      handler(fetchedValue || new Map<string, BeatMapView>());
    });
  }

  subscribeToBeat(beatId: string, handler: (newBeat: Beat|null) => void): string {
    return this.storageService.generateSubscription(
      BeatsService.getBeatKey(beatId),
      (fetchedValue) => {
        if (!fetchedValue) {
          // Beat no longer found, this could occur from being deleted in
          // another tab or component
          return handler(null);
        }

        handler(
          Beat.decode(
            fetchedValue as Uint8Array
          )
        );
      }
    );
  }

  cancelSubscription(subscription: string): void {
    this.storageService.cancelSubscription(subscription);
  }

  async fetchReadView(beatId: string): Promise<BeatReadView[]> {
    const readView: BeatReadView[] = [];

    await this.appendReadView(beatId, readView);

    return readView;
  }

  async appendReadView(beatId: string, currentReadView: BeatReadView[]): Promise<void> {
    const beat = await this.getBeat(beatId) as Beat;

    if (!beat) {
      return;
    }

    const beatReadView = {
      id: beat.id,
      name: beat.synopsis,
      prose: beat.prose,
      showExpanded: (beat.structure.length === 0),
      completeness: beat.completeness
    } as BeatReadView;

    currentReadView.push(beatReadView);

    // Recurse through child beats
    for (const childBeat of beat.structure) {
      await this.appendReadView(childBeat, currentReadView);
    }
  }

  private static removeIdFromParents(beatMap: Map<string, BeatMapView>, id: string, children: string[]): void {
    children.forEach(childId => {
      const childBeat = beatMap.get(childId) as BeatMapView;
      childBeat.parentBeats = childBeat.parentBeats.filter(parentId => parentId !== id);
    });
  }

  private static addParentToChildren(beatMap: Map<string, BeatMapView>, id: string, children: string[]): void {
    children.forEach(childId => {
      const childBeat = beatMap.get(childId) as BeatMapView;
      childBeat.parentBeats.push(id);
    });
  }
}

function allStringsInBothArrays(array1: string[], array2: string[]): boolean {
  if (array1.length !== array2.length) {
    return false;
  }

  return array1.every((value) => {
    return array2.indexOf(value) >= 0;
  });
}

import { Injectable } from '@angular/core';
import { v4 as uuidv4 } from 'uuid';
import {StorageService} from './storage.service';
import {BrainstormTemplate, IBrainstormTemplate} from '../protos';

// Minimal details about a template to show in list view
export interface BrainstormTemplateListView {
  id: string;
  label: string;
}

const ALL_BRAINSTORM_TEMPLATE_LIST_VIEW_KEY = 'allBrainstormTemplatesListView';
const BRAINSTORM_TEMPLATE_KEY_PREFIX = 'brainstormTemplate-';

@Injectable({
  providedIn: 'root'
})
export class BrainstormTemplateService {

  constructor(private storageService: StorageService) { }

  async getAllBrainstormTemplates(): Promise<BrainstormTemplate[]> {
    const allEntries = await this.getAllTemplatesListView();

    const allTemplates: BrainstormTemplate[] = [];
    for (const entry of allEntries) {
      const fetchedTemplate = await this.getBrainstormTemplate(entry.id);
      if (fetchedTemplate != null) {
        allTemplates.push(
          fetchedTemplate
        );
      }
    }

    return allTemplates.filter((value) => !!value);
  }

  async getAllTemplatesListView(): Promise<BrainstormTemplateListView[]> {
    return (await this.storageService.get(ALL_BRAINSTORM_TEMPLATE_LIST_VIEW_KEY) || []) as BrainstormTemplateListView[];
  }

  async setAllTemplatesListView(brainstormTemplates: IBrainstormTemplate[]): Promise<void> {
    const listView = brainstormTemplates.map((brainstormTemplate: IBrainstormTemplate) => {
      return {
        id: brainstormTemplate.id,
        label: brainstormTemplate.label,
      } as BrainstormTemplateListView;
    });

    await this.storageService.set(ALL_BRAINSTORM_TEMPLATE_LIST_VIEW_KEY, listView, true);
  }

  cancelSubscription(subscription: string): void {
    this.storageService.cancelSubscription(subscription);
  }

  getTemplateKey(uuid: string): string {
    return BRAINSTORM_TEMPLATE_KEY_PREFIX + uuid;
  }

  async getBrainstormTemplate(uuid: string): Promise<BrainstormTemplate|null> {
    const fetchedData = (await this.storageService.get(this.getTemplateKey(uuid))) as Uint8Array;

    if (!fetchedData) {
      console.error('Could not find brainstorm template: ' + uuid);
      return null;
    }

    return BrainstormTemplate.decode(
      fetchedData
    );
  }

  async setAllBrainstormTemplates(brainstormTemplates: IBrainstormTemplate[]): Promise<void> {
    await this.setAllTemplatesListView(brainstormTemplates);

    for (const brainstormTemplate of brainstormTemplates) {
      await this.setBrainstormTemplate(brainstormTemplate as BrainstormTemplate);
    }
  }

  async setBrainstormTemplate(brainstormTemplate: BrainstormTemplate, affectsListView: boolean = false): Promise<void> {
    await this.storageService.set(
      this.getTemplateKey(brainstormTemplate.id),
      BrainstormTemplate.encode(brainstormTemplate).finish(),
      true
    );

    if (affectsListView) {
      await this.refreshAllTemplatesListView();
    }
  }

  async refreshAllTemplatesListView(): Promise<void> {
    const allTemplates = await this.getAllBrainstormTemplates();

    await this.setAllTemplatesListView(
      allTemplates.filter((template) => !!template)
    );
  }

  async deleteTemplate(templateId: string): Promise<void> {
    await this.storageService.delete(
      this.getTemplateKey(templateId),
      true
    );

    const listViewEligible = (await this.getAllTemplatesListView())
      .filter((brainstormTemplate) => {
        return brainstormTemplate.id !== templateId;
      });

    await this.setAllTemplatesListView(listViewEligible);
  }

  // Creates a new structure template, and returns the uuid of the new entry
  async createNewBrainstormTemplate(): Promise<string> {
    const uuid = uuidv4();

    const newBrainstormTemplate = BrainstormTemplate.create({
      id: uuid,
      label: 'My new template',
      template: 'This is the placeholder content that will appear when a user selects this.\n\nYou could pose a question here, or give an exercise to explore'
    });

    await this.setBrainstormTemplate(newBrainstormTemplate);

    const allBrainstormTemplates = await this.getAllBrainstormTemplates();
    allBrainstormTemplates.push(newBrainstormTemplate);

    await this.setAllTemplatesListView(allBrainstormTemplates);

    return uuid;
  }

  subscribeToTemplateListView(handler: (newTemplateListView: BrainstormTemplateListView[]) => void): string {
    return this.storageService.generateSubscription(ALL_BRAINSTORM_TEMPLATE_LIST_VIEW_KEY, (fetchedValue) => {
      handler((fetchedValue || []) as BrainstormTemplateListView[]);
    });
  }

  subscribeToTemplate(templateId: string, handler: (newTemplate: BrainstormTemplate) => void): string {
    return this.storageService.generateSubscription(this.getTemplateKey(templateId), (fetchedValue) => {
      handler(
        BrainstormTemplate.decode(
          fetchedValue as Uint8Array
        )
      );
    });
  }
}

import { Injectable } from '@angular/core';
import {WritingWorkspace} from '../protos';
import {WorkspaceMetadataService} from './workspace-metadata.service';
import {StructureTemplateService} from './structure-template.service';
import {StorageService} from './storage.service';
import {TagService} from './tag.service';
import {BeatsService} from './beats.service';
import {BrainstormTemplateService} from './brainstorm-template.service';

const WORKSPACE_NAME_KEY = 'workspaceName';

// This is the monolithic service that'll handle all the story data.
//
// This will certainly get broken up into smaller bits later on.
@Injectable({
  providedIn: 'root'
})
export class MonolithicDataService {

  constructor(private storageService: StorageService,
              private workspaceMetadataService: WorkspaceMetadataService,
              private structureTemplateService: StructureTemplateService,
              private tagService: TagService,
              private beatService: BeatsService,
              private brainstormTemplateService: BrainstormTemplateService) { }

  async newWorkspace(name: string): Promise<void> {
    await this.clear();
    await this.loadWorkspace(WritingWorkspace.create({
      name,
    }));
  }

  // Function to load workspace into local memory.
  async loadWorkspace(workspace: WritingWorkspace): Promise<void> {
    await this.clear();
    await this.setWorkspaceName(workspace.name);
    await this.workspaceMetadataService.setWorkspaceMetadata(workspace.metadata || null);
    await this.structureTemplateService.setAllStructureTemplates(workspace.structureTemplates || []);
    await this.tagService.setAllTags(workspace.tags || []);
    await this.beatService.setAllBeats(workspace.beats || []);
    await this.brainstormTemplateService.setAllBrainstormTemplates(workspace.brainstormTemplates || []);
  }

  // Function to pull workspace from local memory.
  async saveWorkspace(): Promise<WritingWorkspace> {
    const workspace = WritingWorkspace.create();

    workspace.name = await this.getWorkspaceName();
    workspace.metadata = await this.workspaceMetadataService.getWorkspaceMetadata(true);
    workspace.structureTemplates = await this.structureTemplateService.getAllStructureTemplates();
    workspace.tags = await this.tagService.getAllTags();
    workspace.beats = await this.beatService.getAllBeats();
    workspace.brainstormTemplates = await this.brainstormTemplateService.getAllBrainstormTemplates();

    return Promise.resolve(workspace);
  }

  async setWorkspaceName(name: string): Promise<void> {
    await this.storageService.set(WORKSPACE_NAME_KEY, name, true);
  }

  async getWorkspaceName(): Promise<string> {
    return (await this.storageService.get(WORKSPACE_NAME_KEY) as string)
      || '';
  }

  subscribeToWorkspaceName(handler: (workspaceName: string) => void): string {
    return this.storageService.generateSubscription(WORKSPACE_NAME_KEY, (result) => {
      handler(result as string);
    });
  }

  cancelSubscriptionToWorkspaceName(subscriptionId: string): void {
    this.storageService.cancelSubscription(subscriptionId);
  }

  async clear(): Promise<void> {
    await this.storageService.clearAll();
  }
}

import { Injectable } from '@angular/core';
import {StorageMap} from '@ngx-pwa/local-storage';
import * as hash from 'object-hash';
import { v4 as uuidv4 } from 'uuid';

interface StorageSubscription {
  subscriptionKey: string;
  storageKey: string;
  lastHashValue: string;
  callback: (updatedValue: any) => void;
}

const POLL_FREQUENCY = 250;

@Injectable({
  providedIn: 'root'
})
export class StorageService {

  // subscriptions
  private subscriptions: Map<string, StorageSubscription> = new Map();

  constructor(private storage: StorageMap) {
    setInterval(async () => {
      for (const subscription of this.subscriptions.values()) {
        await this.checkSubscription(subscription);
      }
    }, POLL_FREQUENCY);
  }

  async get(key: string): Promise<any> {
    return await this.storage.get(key).toPromise();
  }

  async set(key: string, value: any, isSubscribable: boolean = false): Promise<void> {

    await this.storage.set(key, value).toPromise();

    if (isSubscribable) {
      // Store the hash value, so that other tabs can poll for the value and detect changes
      // that they didn't necessarily make
      const hashValue = hash(value, {algorithm: 'sha1'});
      await this.storage.set(this.getHashKey(key), hashValue).toPromise();
    }
  }

  generateSubscription(storageKey: string, callback: (updatedValue: any) => void): string {
    const subscriptionUuid = uuidv4();

    const newSubscription = {
      storageKey,
      callback,
      subscriptionKey: subscriptionUuid,
      lastHashValue: ''
    } as StorageSubscription;

    this.subscriptions.set(subscriptionUuid, newSubscription);

    // Kick off subscription so there's not a weird POLL_FREQUENCY delay
    setTimeout(async () => {
      await this.checkSubscription(newSubscription);
    }, 1);

    return subscriptionUuid;
  }

  cancelSubscription(subscriptionKey: string): void {
    this.subscriptions.delete(subscriptionKey);
  }

  async checkSubscription(subscription: StorageSubscription): Promise<void> {
    const fetchedHashValue = await this.storage.get(this.getHashKey(subscription.storageKey)).toPromise();
    if (!fetchedHashValue) {
      console.error('Subscription checked on storage key ' + subscription.storageKey
        + ', please make sure it is being set with isSubscribable=true');
      return;
    }

    const newHashValue = fetchedHashValue as string;
    if (newHashValue === subscription.lastHashValue) {
      return;
    }

    subscription.callback(
      await this.get(subscription.storageKey)
    );

    subscription.lastHashValue = newHashValue;

    this.subscriptions.set(subscription.subscriptionKey, subscription);
  }

  getHashKey(key: string): string {
    return key + '-sha1';
  }

  async clearAll(): Promise<void> {
    await this.storage.clear().toPromise();
  }

  async delete(key: string, isSubscribable: boolean = false): Promise<void> {
    if (isSubscribable) {
      // Notify subscriptions that the value no longer exists
      await this.storage.set(this.getHashKey(key), 'nolongervalid').toPromise();
    }

    await this.storage.delete(key).toPromise();
  }
}

import { Injectable } from '@angular/core';
import { v4 as uuidv4 } from 'uuid';
import {IStructureTemplate, StructureTemplate} from '../protos';
import {StorageService} from './storage.service';
import StructureTemplateBeat = StructureTemplate.StructureTemplateBeat;

// Minimal details about a template to show in list view
export interface StructureTemplateListView {
  id: string;
  name: string;
}

const ALL_STRUCTURE_TEMPLATE_LIST_VIEW_KEY = 'allStructureTemplatesListView';
const STRUCTURE_TEMPLATE_KEY_PREFIX = 'structureTemplate-';

@Injectable({
  providedIn: 'root'
})
export class StructureTemplateService {

  constructor(private storageService: StorageService) { }

  async getAllStructureTemplates(): Promise<StructureTemplate[]> {
    const allEntries = await this.getAllTemplatesListView();

    const allTemplates: StructureTemplate[] = [];
    for (const entry of allEntries) {
      const fetchedTemplate = await this.getStructureTemplate(entry.id);
      if (fetchedTemplate != null) {
        allTemplates.push(
          fetchedTemplate
        );
      }
    }

    return allTemplates.filter((value) => !!value);
  }

  async getAllTemplatesListView(): Promise<StructureTemplateListView[]> {
    return (await this.storageService.get(ALL_STRUCTURE_TEMPLATE_LIST_VIEW_KEY) || []) as StructureTemplateListView[];
  }

  async setAllTemplatesListView(structureTemplates: IStructureTemplate[]): Promise<void> {
    const listView = structureTemplates.map((structureTemplate: IStructureTemplate) => {
      return {
        id: structureTemplate.id,
        name: structureTemplate.name,
      } as StructureTemplateListView;
    });

    await this.storageService.set(ALL_STRUCTURE_TEMPLATE_LIST_VIEW_KEY, listView, true);
  }

  subscribeToTemplateListView(handler: (newTemplateListView: StructureTemplateListView[]) => void): string {
    return this.storageService.generateSubscription(ALL_STRUCTURE_TEMPLATE_LIST_VIEW_KEY, (fetchedValue) => {
      handler((fetchedValue || []) as StructureTemplateListView[]);
    });
  }

  cancelSubscription(subscription: string): void {
    this.storageService.cancelSubscription(subscription);
  }

  getTemplateKey(uuid: string): string {
    return STRUCTURE_TEMPLATE_KEY_PREFIX + uuid;
  }

  async getStructureTemplate(uuid: string): Promise<StructureTemplate|null> {
    const fetchedData = (await this.storageService.get(this.getTemplateKey(uuid))) as Uint8Array;

    if (!fetchedData) {
      console.error('Could not find structure template: ' + uuid);
      return null;
    }

    return StructureTemplate.decode(
      fetchedData
    );
  }

  async setAllStructureTemplates(structureTemplates: IStructureTemplate[]): Promise<void> {
    await this.setAllTemplatesListView(structureTemplates);

    for (const structureTemplate of structureTemplates) {
      await this.setStructureTemplate(structureTemplate as StructureTemplate);
    }
  }

  async setStructureTemplate(structureTemplate: StructureTemplate, affectsListView: boolean = false): Promise<void> {
    await this.storageService.set(
      this.getTemplateKey(structureTemplate.id),
      StructureTemplate.encode(structureTemplate).finish(),
      true
    );

    if (affectsListView) {
      await this.refreshAllTemplatesListView();
    }
  }

  subscribeToTemplate(templateId: string, handler: (newTemplate: StructureTemplate) => void): string {
    return this.storageService.generateSubscription(this.getTemplateKey(templateId), (fetchedValue) => {
      handler(
        StructureTemplate.decode(
          fetchedValue as Uint8Array
        )
      );
    });
  }

  async refreshAllTemplatesListView(): Promise<void> {
    const allTemplates = await this.getAllStructureTemplates();

    await this.setAllTemplatesListView(
      allTemplates.filter((template) => !!template)
    );
  }

  async deleteTemplate(templateId: string): Promise<void> {
    await this.storageService.delete(
      this.getTemplateKey(templateId),
      true
    );

    const listViewEligible = (await this.getAllTemplatesListView())
      .filter((structureTemplate) => {
        return structureTemplate.id !== templateId;
    });

    await this.setAllTemplatesListView(listViewEligible);
  }

  // Creates a new structure template, and returns the uuid of the new entry
  async createNewStructureTemplate(): Promise<string> {
    const uuid = uuidv4();

    const newStructureTemplate = StructureTemplate.create({
      id: uuid,
      name: 'My new template',
      description: 'Description goes here',
      beats: [
        StructureTemplateBeat.create({
          description: 'This is the first beat',
          intendedDurationMs: 5
        })
      ]
    });

    await this.setStructureTemplate(newStructureTemplate);

    const allStructureTemplates = await this.getAllStructureTemplates();
    allStructureTemplates.push(newStructureTemplate);

    await this.setAllTemplatesListView(allStructureTemplates);

    return uuid;
  }

}

import { Injectable } from '@angular/core';
import { v4 as uuidv4 } from 'uuid';
import {StorageService} from './storage.service';
import {ITag, Tag} from '../protos';

// Minimal details about a template to show in list view
export interface TagListView {
  id: string;
  name: string;
}

const ALL_TAG_LIST_KEY = 'allTagList';
const TAG_KEY_PREFIX = 'tag-';

@Injectable({
  providedIn: 'root'
})
export class TagService {

  constructor(private storageService: StorageService) { }

  async getAllTags(): Promise<Tag[]> {
    const allEntries = await this.getAllTagsListView();

    const allTags: Tag[] = [];

    for (const entry of allEntries) {
      const fetchedTag = await this.getTag(entry.id);
      if (fetchedTag != null) {
        allTags.push(
          fetchedTag
        );
      }
    }

    return allTags;
  }

  async getAllTagsListView(): Promise<TagListView[]> {
    return (await this.storageService.get(ALL_TAG_LIST_KEY) || []) as TagListView[];
  }

  getTagKey(uuid: string): string {
    return TAG_KEY_PREFIX + uuid;
  }

  async getTag(uuid: string): Promise<Tag|null> {
    const fetchedData = (await this.storageService.get(this.getTagKey(uuid))) as Uint8Array;

    if (!fetchedData) {
      console.error('Could not find structure template: ' + uuid);
      return null;
    }

    return Tag.decode(
      fetchedData
    );
  }

  subscribeToTagListView(handler: (newTagListView: TagListView[]) => void): string {
    return this.storageService.generateSubscription(ALL_TAG_LIST_KEY, (fetchedValue) => {
      handler((fetchedValue || []) as TagListView[]);
    });
  }

  subscribeToTag(tagId: string, handler: (newTag: Tag|null) => void): string {
    return this.storageService.generateSubscription(this.getTagKey(tagId), (fetchedValue) => {
      handler(
        Tag.decode(
          fetchedValue as Uint8Array
        )
      );
    });
  }

  async deleteTag(tagId: string): Promise<void> {
    await this.storageService.delete(
      this.getTagKey(tagId),
      true
    );

    const listViewEligible = (await this.getAllTagsListView())
      .filter((tagListView) => {
        return tagListView.id !== tagId;
      });

    await this.setAllTagsListView(listViewEligible);
  }

  cancelSubscription(subscription: string): void {
    this.storageService.cancelSubscription(subscription);
  }

  async setAllTagsListView(tags: ITag[]): Promise<void> {
    const listView = tags.map((tag: ITag) => {
      return {
        id: tag.id,
        name: tag.name,
      } as TagListView;
    });

    await this.storageService.set(ALL_TAG_LIST_KEY, listView, true);
  }

  async refreshAllTagsListView(): Promise<void> {
    const allTags = await this.getAllTags();

    await this.setAllTagsListView(
      allTags
    );
  }

  async setAllTags(tags: ITag[]): Promise<void> {
    await this.setAllTagsListView(tags);

    for (const tag of tags) {
      await this.setTag(tag as Tag);
    }
  }

  async setTag(tag: Tag, affectsListView: boolean = false): Promise<void> {
    await this.storageService.set(
      this.getTagKey(tag.id),
      Tag.encode(tag).finish(),
      true
    );

    if (affectsListView) {
      await this.refreshAllTagsListView();
    }
  }

  // Creates a new structure template, and returns the uuid of the new entry
  async createNewTag(): Promise<string> {
    const uuid = uuidv4();

    const newTag = Tag.create({
      id: uuid,
      name: 'My new tag',
      description: 'Description goes here',
      hasNumericValue: true,
    });

    await this.setTag(newTag);

    const allTags = await this.getAllTags();
    allTags.push(newTag);

    await this.setAllTagsListView(allTags);

    return uuid;
  }
}

import { Injectable } from '@angular/core';
import {
  Duration,
  IDuration,
  ITimestamp,
  IWritingSession,
  IWritingWorkspaceMetadata,
  Timestamp,
  WritingSession,
  WritingWorkspaceMetadata} from '../protos';
import {StorageService} from './storage.service';

const CURRENT_SESSION_KEY = 'currentSession';
const LAST_HEARTBEAT_KEY = 'lastHeartbeat';
const LAST_ACTIVE_HEARTBEAT_KEY = 'lastActiveHeartbeat';
const WRITING_WORKSPACE_METADATA_KEY = 'workspaceMetadata';

const SESSION_TIMEOUT_MS = 1000 *  60; // Allow 1 minute of the browser being closed before you mark session as inactive
const SESSION_ACTIVE_TIMEOUT_MS = 5000; // Allow 5 seconds of the tool being not-visible before we mark as inactive

@Injectable({
  providedIn: 'root'
})
export class WorkspaceMetadataService {

  constructor(private storageService: StorageService) { }

  subscribeToWorkspaceMetadata(handler: (workspaceMetadata: WritingWorkspaceMetadata) => void): string {
    return this.storageService.generateSubscription(WRITING_WORKSPACE_METADATA_KEY, (result) => {
      if (result) {
        handler(WritingWorkspaceMetadata.decode(result as Uint8Array));
      } else {
        handler(WritingWorkspaceMetadata.create());
      }
    });
  }

  cancelSubscriptionToWorkspaceMetadata(subscriptionKey: string): void {
    this.storageService.cancelSubscription(subscriptionKey);
  }

  async getWorkspaceMetadata(appendCurrentSession: boolean): Promise<WritingWorkspaceMetadata> {
    const fetchedData = (await this.storageService.get(WRITING_WORKSPACE_METADATA_KEY)) as Uint8Array;
    let workspaceMetadata;
    if (fetchedData) {
      workspaceMetadata = WritingWorkspaceMetadata.decode(fetchedData);
    } else {
      workspaceMetadata = WritingWorkspaceMetadata.create();
    }

    if (appendCurrentSession) {
      const currentSession = await this.getCurrentSession() as IWritingSession;

      workspaceMetadata.sessionHistory = [currentSession].concat(workspaceMetadata.sessionHistory);
    }

    return workspaceMetadata;
  }

  async setWorkspaceMetadata(workspaceMetadata: IWritingWorkspaceMetadata|null): Promise<void> {
    if (!workspaceMetadata) {
      workspaceMetadata = WritingWorkspaceMetadata.create();
    }

    await this.storageService.set(
      WRITING_WORKSPACE_METADATA_KEY,
      WritingWorkspaceMetadata.encode(workspaceMetadata).finish(),
      true
    );
  }

  async saveCurrentSessionToHistory(): Promise<void> {
    await this.setWorkspaceMetadata(
      await this.getWorkspaceMetadata(true)
    );
  }

  // Let the app know the current writing session is still active.
  async updateSessionHeartbeat(): Promise<WritingSession> {
    const isActive = document.visibilityState === 'visible';

    let currentSession = await this.getCurrentSession();
    const lastHeartbeat = await this.storageService.get(LAST_HEARTBEAT_KEY) as number;
    const lastActiveHeartbeat = await this.storageService.get(LAST_ACTIVE_HEARTBEAT_KEY) as number;

    if (!currentSession) {
      currentSession = this.createNewWritingSession();
    } else if (Date.now() - lastHeartbeat > SESSION_TIMEOUT_MS) {
      // Last session is stale, add it to history and initialize a new one
      await this.saveCurrentSessionToHistory();

      currentSession = this.createNewWritingSession();
    }

    currentSession.duration = getDurationBetweenTimestamps(
      currentSession.start as ITimestamp,
      getTimestampFromEpochMS(Date.now())
    );

    if (isActive) {
      const activeDelta = Date.now() - lastActiveHeartbeat;
      if (activeDelta < SESSION_ACTIVE_TIMEOUT_MS) {
        const activeDuration = currentSession.activeDuration || Duration.create({seconds: 0, nanos: 0});

        currentSession.activeDuration = incrementDuration(activeDuration, activeDelta);
      }

      await this.storageService.set(LAST_ACTIVE_HEARTBEAT_KEY, Date.now());
    }

    const encoded = WritingSession.encode(currentSession).finish();
    await this.storageService.set(CURRENT_SESSION_KEY, encoded);
    await this.storageService.set(LAST_HEARTBEAT_KEY, Date.now());

    return currentSession;
  }

  createNewWritingSession(): WritingSession {
    const session = WritingSession.create();
    session.start = getTimestampFromEpochMS(Date.now());
    return session;
  }

  async getCurrentSession(): Promise<WritingSession|null> {
    const current = await this.storageService.get(CURRENT_SESSION_KEY);
    if (!current) {
      return null;
    }

    return WritingSession.decode(current as Uint8Array);
  }
}


function getTimestampFromEpochMS(epochMS: number): Timestamp {
  return Timestamp.create({
    seconds: Math.floor(epochMS / 1000),
    nanos: (epochMS % 1000) * 1e6
  });
}

function getDurationBetweenTimestamps(start: ITimestamp, end: ITimestamp): Duration {
  let nanosDiff = (end.nanos as number) - (start.nanos as number);
  let secondsDiff = (end.seconds as number) - (start.seconds as number);

  while (nanosDiff < 0) {
    secondsDiff -= 1;
    nanosDiff += 1e9;
  }
  while (nanosDiff > 1e9) {
    nanosDiff -= 1e9;
    secondsDiff += 1;
  }
  return Duration.create({
    seconds: secondsDiff,
    nanos: nanosDiff
  });
}

function incrementDuration(duration: IDuration, deltaMs: number): IDuration {
  let nanos = (duration.nanos || 0) + (deltaMs * 1e6);
  let seconds = (duration.seconds || 0);

  while (nanos > 1e9) {
    nanos -= 1e9;
    seconds = (seconds as number) + 1;
  }
  return Duration.create({
    seconds,
    nanos
  });
}

