  }

  async onBrainstormTemplateUpdate(newValue: BrainstormTemplateUpdate): Promise<void> {
    await this.brainstormTemplateService.setBrainstormTemplate(newValue.brainstormTemplate, newValue.modifiesListView);
  }
}

