  constructor() { }

  ngOnInit(): void {
    this.isExpanded = this.beat?.showExpanded || false;
    this.paragraphs = this.beat?.prose.split('\n') || [];
  }

  toggleExpansion(): void {
    this.isExpanded = !this.isExpanded;
  }

