    switch (this.beat?.completeness as Completeness) {
      case Completeness.FINAL:
        return 'final';
      case Completeness.POLISHED:
        return 'polished';
      case Completeness.INITIAL_DRAFT:
        return 'initial-draft';
      case Completeness.BRAINSTORM:
        return 'brainstorm';
    }
    return 'not-started';
  }
}

