export interface TagUpdate {
  tag: Tag;
  modifiesListView: boolean;
}

@Component({
  selector: 'app-tag-details',
  templateUrl: './tag-details.component.html',
  styleUrls: ['./tag-details.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class TagDetailsComponent implements OnInit {

  constructor() { }

  @Input()
  tag: Tag|null = null;

  @Output() tagUpdated = new EventEmitter<TagUpdate>();

  onNameInput = debounce((event: any) => {
    const tag = this.tag as Tag;

    tag.name = event.target.value;

    this.tagUpdated.emit({
      tag,
      modifiesListView: true,
    } as TagUpdate);
  }, 200);

  onDescriptionInput = debounce((event: any) => {
    const tag = this.tag as Tag;

    tag.description = event.target.value;

    this.tagUpdated.emit({
      tag,
      modifiesListView: false,
    } as TagUpdate);
  }, 200);

  setEnumLabel = debounce((index: number, event: any) => {
    const tag = this.tag as Tag;

    tag.enumValues[index].label = event.target.value;

    this.tagUpdated.emit({
      tag,
      modifiesListView: false,
    } as TagUpdate);
  }, 200);

  ngOnInit(): void {
  }

  hasNumericValue(): boolean {
    return this.tag?.hasNumericValue ?? false;
  }

  onHasNumericValueCheckbox(event: MatCheckboxChange): void {
    const tag = this.tag as Tag;

    tag.hasNumericValue = event.checked;

    this.tagUpdated.emit({
      tag,
      modifiesListView: false,
    } as TagUpdate);
  }

  isEmpty(): boolean {
    return this.tag == null;
  }

  setDeprecatedEnum(index: number, event: MatCheckboxChange): void {
    const isDeprecated = !event.checked;

    const tag = this.tag as Tag;

    tag.enumValues[index].isDeprecated = isDeprecated;

    this.tagUpdated.emit({
      tag,
      modifiesListView: false,
    } as TagUpdate);
  }

  addEnumValue(): void {
    const enumValue = {
      label: 'My enum option',
      isDeprecated: false,
    } as Tag.EnumValue;

    const tag = this.tag as Tag;

    tag.enumValues.push(enumValue);

    this.tagUpdated.emit({
      tag,
      modifiesListView: false,
    } as TagUpdate);
  }
}

import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import {TagListView} from '../../tag.service';

@Component({
  selector: 'app-tag-nav',
  templateUrl: './tag-nav.component.html',
  styleUrls: ['./tag-nav.component.scss']
})
export class TagNavComponent implements OnInit {

  @Input()
  selectedTagId = '';

  @Input()
  tagListView: TagListView[] = [];

  @Output() newTag = new EventEmitter<void>();

  @Output() selectTag = new EventEmitter<string>();

  @Output() deleteTag = new EventEmitter<void>();

  constructor() { }

  ngOnInit(): void {
  }

  createNewTemplate(): void {
    this.newTag.emit();
  }

  canDelete(): boolean {
    return (this.selectedTagId || '').length > 0;
  }
}

import {ChangeDetectionStrategy, ChangeDetectorRef, Component, OnDestroy, OnInit} from '@angular/core';
import {TagListView, TagService} from '../tag.service';
import {Tag} from '../../protos';
import {TagUpdate} from './tag-details/tag-details.component';
import {ActivatedRoute, Router} from '@angular/router';

@Component({
  selector: 'app-tag-page',
  templateUrl: './tag-page.component.html',
  styleUrls: ['./tag-page.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class TagPageComponent implements OnInit, OnDestroy {

  selectedTagId = '';

  selectedTag: Tag|null = null;
  selectedTagSubscription = '';

  tagListView: TagListView[] = [];
  tagListViewSubscription = '';

  constructor(private tagService: TagService, private ref: ChangeDetectorRef, private route: ActivatedRoute, private router: Router) { }

  ngOnInit(): void {
    // Read the selected beat ID from the route
    this.route.params.subscribe(async (value) => {
      const selectedId = value.id as string;
      if (this.selectedTagId !== selectedId && !!selectedId) {
        await this.selectTag(selectedId);
      }
    });

    this.tagListViewSubscription = this.tagService.subscribeToTagListView((newValue) => {
      this.tagListView = newValue;
      this.ref.markForCheck();
    });
  }

  ngOnDestroy(): void {
    this.tagService.cancelSubscription(this.tagListViewSubscription);
    this.tagService.cancelSubscription(this.selectedTagSubscription);
  }

  async newTag(): Promise<void> {
    await this.selectTag(
      await this.tagService.createNewTag()
    );
  }

  async selectTag(tagId: string): Promise<void> {
    this.selectedTagId = tagId;

    // Clear old subscription setup new one
    this.tagService.cancelSubscription(this.selectedTagSubscription);
    if (!!tagId.length) {
      this.selectedTagSubscription = this.tagService.subscribeToTag(tagId, (newValue) => {
        this.selectedTag = newValue;
        this.ref.markForCheck();
      });
    }

    await this.router.navigate(['/tags', { id: tagId }]);

    this.ref.markForCheck();
  }

  async deleteTag(): Promise<void> {
    const tagToDelete = this.selectedTagId;
    this.selectedTag = null;
    await this.selectTag('');
    await this.tagService.deleteTag(tagToDelete);
  }

  async onTagChanges(newValue: TagUpdate): Promise<void> {
    await this.tagService.setTag(newValue.tag, newValue.modifiesListView);
  }
}

